import React, { useState, useEffect } from 'react';
import './App.css';
import Loading from './components/LoadingPage';
import Home from './components/Home';
import Navbar from './components/Nav';
import Works from './components/Works';
import Expertise from './components/Expertise';
import Contact from './components/Contact';
import Footer from './components/Footer';

import BoltIcon from '@mui/icons-material/Bolt';

function App() {
  const [ isLoading, setIsLoading ] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []); 

  return (
    <body className="scrollbar bg-gray-950  text-light h-screen w-screen lg:px-40 px-8 py-2 overflow-hidden">
      {isLoading && <Loading />}

      {!isLoading && 
      <div className='bg-dark h-full scrollbar overflow-y-scroll overflow-x-hidden rounded-lg shadow'>

        <div className='w-full h-fit bg-primary text-dark/90 rounded-t-lg text-center flex justify-center p-1 sticky top-0 z-50 text-xs'>
          <button className='font-semibold mono'>BRING YOUR VISIONS TO LIFE</button>

          <span className='absolute right-1 top-0 flex items-center animate-pulse'>
            <BoltIcon className='pl-1 pb-1 icon text-green-500' />
            <p className='pr-1 hidden md:block text-xs'>Available for Hire</p>
          </span>
        </div>

        <Navbar />

        <section id='0' className='view px-10'>
          <Home />
        </section>

        <section id='1' className='md:px-10 pt-16'>
          <Expertise />
        </section>

          <section id='2' className='md:view px-10 pt-16'>
          <Works />
        </section>

          <section id='3' className='md:view px-10 pt-16 pb-10'>
          <Contact />
        </section>

        <div className='px-10 py-5 bottom-0'>
          <Footer />
        </div>
      </div>
      }
    </body>
  );
}

export default App;
