import React, { useState, useEffect } from 'react';
import LunchDiningIcon from '@mui/icons-material/LunchDining';
import CloseRoundedIcon from '@mui/icons-material/CloseRounded';

const Nav = () => {
  const [activeSection, setActiveSection] = useState('0');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setIsMenuOpen(false);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['0', '1', '2', '3'];
      const scrollPosition = window.scrollY + 200;
      for (const section of sections) {
        const currentSection = document.getElementById(section);
        if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
          setActiveSection(section);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleReload = () => {
    window.location.reload();
    window.scrollTo(0, 0);
  };

  return (
    <div className='backdrop-blur-3xl flex justify-between items-center px-6 md:px-10 py-1 font-semibold sticky top-6 z-50'>
      <div>
        <button onClick={handleReload} className="text-2xl font-serif">D.</button>
      </div>

      <div className=''>
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className='text-2xl hover:text-primary'
        >
          {isMenuOpen ? <CloseRoundedIcon /> : <LunchDiningIcon />}
        </button>
      </div>

      {isMenuOpen && (
        <nav className='flex text-base w-full md:w-fit absolute top-10 right-0 md:top-1 md:right-16 px-10 md:px-2'>
          <ul className='w-full flex flex-col md:flex-row gap-4 bg-dark/90 md:bg-transparent rounded-lg px-4 py-4 md:py-1 items-center mono'>
            {/* <li className={`{activeSection === '0' ? 'active' : ''} hover:text-primary cursor-pointer button.clicked`}>
              <button onClick={() => scrollToSection('0')} >
                about
              </button>
            </li> */}

            <li className={`{activeSection === '1' ? 'active' : ''} hover:text-primary cursor-pointer button.clicked`}>
              <button onClick={() => scrollToSection('1')} >
                expertise
              </button>
            </li>

            <li className={`{activeSection === '2' ? 'active' : ''} hover:text-primary cursor-pointer button.clicked`}>
              <button onClick={() => scrollToSection('2')} >
                projects
              </button>
            </li>

            <li className={`{activeSection === '3' ? 'active' : ''} hover:text-primary cursor-pointer button.clicked`}>
              <button onClick={() => scrollToSection('3')} >
                contact
              </button>
            </li>

          </ul>
        </nav>
      )}
    </div>
  );
}

export default Nav;
