import React, { useState } from 'react';

import ArrowCircleUpIcon from '@mui/icons-material/ArrowCircleUp';
import ArrowCircleUpTwoToneIcon from '@mui/icons-material/ArrowCircleUpTwoTone';

const Footer = () => {
    const [ showIcon, setShowIcon ] = useState(false);

    return (
        <div className="bg-accent p-10 shadow">

            <div className="flex justify-evenly">

            <div className="bg-bg1 w-1/2">
                <p className="text-xl font-semibold">DIANNE MAE P FABIANO</p>
                <p>Web Developer</p>
            </div>

            <div className="w-1/2 bg-bg2">
                socials 
            </div>

            </div>

            <div
                className="pt-6 flex justify-center"
                onMouseEnter={() => setShowIcon(true)}
                onMouseLeave={() => setShowIcon(false)}            
            >
                <ArrowCircleUpIcon className='text-primary hover:text-secondary animate-bounce' />
                
                {showIcon && <ArrowCircleUpTwoToneIcon className='text-secondary animate-bounce' />}
            </div>

        </div>
    );
};

export default Footer;
