import React, { useEffect, useState } from 'react';

import iconImage from '../img/pfp.jpg';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import KeyboardArrowLeftRoundedIcon from '@mui/icons-material/KeyboardArrowLeftRounded';
import KeyboardArrowRightRoundedIcon from '@mui/icons-material/KeyboardArrowRightRounded';

const name = "Dianne Mae P Fabiano";

const Header = () => {
    const [ dipslayText, setDisplayText ] = useState('');
    const [ currentIndex, setCurrentIndex ] = useState(0);

    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (currentIndex <= name.length) {
                setDisplayText(name.slice(0, currentIndex));
                setCurrentIndex((prevIndex) => prevIndex + 1);
            } else {
                clearInterval(typingInterval);
            }
        }, 300)
        return () => clearInterval(typingInterval);
    }, [currentIndex]);

    return (
        <div className="h-fit py-6 md:px-24 px-8 flex">
            <div className='aspect-square md:h-32 h-24 hover:border-2'>
                <img src={iconImage} className="rounded-full h-full" />
            </div>

            <div className='w-full h-full px-10 flex flex-col justify-center gap-1'>
                <p className='font-black md:text-xl'>{dipslayText}</p>
                <p className='font-semibold animate-pulse'>
                    <span>
                        <KeyboardArrowLeftRoundedIcon />
                    </span>
                    Web Developer
                    <span>
                        <KeyboardArrowRightRoundedIcon />
                    </span>
                </p>

                <div className='flex flex-col gap-2'>
                   <div>
                        <button className='md:text-base text-sm px-4 py-1 my-2 rounded-xl font-semibold bg-violet-400 text-white hover:bg-slate-500 hover:text-white hover:drop-shadow-sm'>
                            Message
                        </button>
                    </div>

                    <div className='flex gap-3'>
                        <a
                            href='https://www.linkedin.com/in/dianne-fabiano/'
                            className='text-slate-500 hover:text-slate-600 hover:drop-shadow-sm'
                        >
                            <LinkedInIcon />
                        </a>

                        <a
                            href='https://github.com/fabianodianne'
                            className='text-slate-500 hover:text-slate-600 hover:drop-shadow-sm'
                        >
                            <GitHubIcon />
                        </a>

                    </div>
                </div>
            </div>
        </div>
    );
};

export default Header;
