import React, { useState, useEffect } from 'react';

import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import SourceRoundedIcon from '@mui/icons-material/SourceRounded';
import IntegrationInstructionsRoundedIcon from '@mui/icons-material/IntegrationInstructionsRounded';
import RingVolumeRoundedIcon from '@mui/icons-material/RingVolumeRounded';

function Navbar() {
  const [activeSection, setActiveSection] = useState('0');

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['0', '1', '2', '3'];
      const scrollPosition = window.scrollY + 200;
      for (const section of sections) {
        const currentSection = document.getElementById(section);
        if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
          setActiveSection(section);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className=' backdrop-blur-3xl bg-white/70 flex justify-center p-1 sticky top-6 md:text-sm text-xs font-semibold z-50'>
      <ul className='flex gap-10 md:gap-16 text-primary'>
        
        <li className={`{activeSection === '0' ? 'active' : ''} hover:text-secondary cursor-pointer`}>
          <button
            onClick={() => scrollToSection('0')}
          >
            <span className='w-full flex justify-center md:hidden'>
              <HomeRoundedIcon />
            </span>
            Home
          </button>
        </li>

        <li className={`{activeSection === '1' ? 'active' : ''} hover:text-secondary cursor-pointer`}>
          <button
            onClick={() => scrollToSection('1')}
          >
            <span className='w-full flex justify-center md:hidden'>
              <SourceRoundedIcon />
            </span>
            Services
          </button>
        </li>

        <li className={`{activeSection === '2' ? 'active' : ''} hover:text-secondary cursor-pointer`}>
          <button
            onClick={() => scrollToSection('2')}
          >
            <span className='w-full flex justify-center md:hidden'>
              <IntegrationInstructionsRoundedIcon />
            </span>
            Projects
          </button>
        </li>

        <li className={`{activeSection === '3' ? 'active' : ''} hover:text-secondary cursor-pointer`}>
          <button
            onClick={() => scrollToSection('3')}
          >
            <span className='w-full flex justify-center md:hidden'>
              <RingVolumeRoundedIcon />
            </span>
            Contact
          </button>
        </li>
      </ul>
    </nav>
  );
}

export default Navbar;
