import './App.css';
import Header from './components/Header';
import Navbar from './components/Nav';
import Home from './components/Home';
import Works from './components/Works';
import Services from './components/Services';
import Contact from './components/Contact';
import Footer from './components/Footer';

import BoltIcon from '@mui/icons-material/Bolt';

function App() {

  const handleReload = () => {
    window.location.reload();
    window.scrollTo(0, 0);
  };

  return (
    <body className=" scrollbar bg-accent h-screen w-screen lg:px-40 px-8 py-4 overflow-hidden bg-center">
      <div className='scrollbar bg-white backdrop-blur-sm h-full overflow-y-scroll overflow-x-hidden rounded-lg text-dark  drop-shadow-md border text-sm md:text-base'>
        <div className='w-full bg-bg2 rounded-t-lg text-center py-1 sticky top-0 z-50 text-xs'>
          <button onClick={handleReload} className='font-semibold '>BRING YOUR VISIONS TO LIFE</button>
          <span className='absolute right-1 top-0 flex items-center animate-pulse'>
            <BoltIcon className='pl-1 icon text-green-500' />
            <p className='pr-1 hidden md:block'>Available for Hire</p>
          </span>
        </div>

        <div>
          <Header />

          <Navbar />

          <div>
            <section id='0' className='px-10 pt-16 h'>
              <Home />
            </section>

            <section id='1' className='px-10 pt-16'>
              <Services />
            </section>

            <section id='2' className='px-10 pt-16'>
              <Works />
            </section>

            <section id='3' className='px-10 pt-16 pb-10'>
              <Contact />
            </section>

            <section className='px-10 py-5 bg-white/50'>
              <Footer />
            </section>

          </div>
        </div>
      </div>

    </body>
  );
}

export default App;
