import React, { useState, useEffect, useRef } from 'react';

import './App.css';
import backgroundImage from './img/bg.png';
import Header from './components/Header';
import Navbar from './components/Nav';
import Home from './components/Home';
import Works from './components/Works';
import Contact from './components/Contact';

import SaveAltRoundedIcon from '@mui/icons-material/SaveAltRounded';

function App() {
  const [showParagraph, setShowParagraph ] = useState(false);

  return (
    <div className="scrollbar h-screen w-screen xl:px-60 lg:px-40 px-8 py-4 overflow-hidden">
      <div className='scrollbar bg-white h-full overflow-y-scroll rounded-lg text-slate-600 font-mono drop-shadow-md border'>
        <div className='w-full bg-gray-50 rounded-t-lg text-center py-1 sticky top-0 z-50'>
          <p className='font-semibold text-xs'>BRING YOUR VISIONS TO LIFE</p>
          <span className='absolute right-1 top-0 flex items-center'>
            {showParagraph && (
              <p className='text-xs px-1'>Download Resume</p>
            )}
            <a

              className='text-slate-400 hover:text-slate-500 cur'
              onMouseEnter={() => setShowParagraph(true)}
              onMouseLeave={() => setShowParagraph(false)}

            >
            <SaveAltRoundedIcon />
            </a>
          </span>
        </div>

        <div>
          <Header />

          <Navbar />

          <div className=''>
            <section id='0' className='border-t border-slate-200'>
              <Home />
            </section>

            <section id='1' className='border-t border-slate-200'>
              <Works />
            </section>

            <section id='2' className='border-t border-slate-200'>
              <Contact />
            </section>

          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
