import React, { useState } from "react";

import SendRoundedIcon from '@mui/icons-material/SendRounded';

const Contact = () => {
    const [ formData, setFormData ] = useState({
        name: '',
        email: '',
        subject: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormData({ name: '', email: '', subject: '', message: ''})
    }

    return (
        <div className="grid md:grid-cols-3 p-10 py-20 gap-4"> 
            <div>
                <p className="text-md font-semibold">Interested in collaborating with me?</p>
                <p className="text-sm">Let's turn your ideas into reality!</p>

            </div>

            <div className="md:col-span-2 shadow hover:shadow-md rounded-md">
                <form onSubmit={handleSubmit}>
                    <div className="p-4">
                        <input
                            className="border border-slate-300 rounded-xl w-full p-1 px-2 my-2 shadow focus:outline-none"
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            placeholder="Name"
                            required
                        />

                        <input
                            className="border border-slate-300 rounded-xl w-full p-1 px-2 my-2 shadow focus:outline-none"
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            placeholder="Email"
                            required
                        />

                        <input
                            className="border border-slate-300 rounded-xl w-full p-1 px-2 my-2 shadow focus:outline-none"
                            type="subject"
                            id="subject"
                            name="subject"
                            value={formData.subject}
                            onChange={handleChange}
                            placeholder="Subject"
                            required
                        />

                        <textarea
                            className="border border-slate-300 rounded-xl w-full p-1 px-2 my-2 shadow focus:outline-none min-h-36 scro"
                            id="message"
                            name="message"
                            value={formData.message}
                            onChange={handleChange}
                            placeholder="Message"
                            required
                        ></textarea>

                        <button
                            type="submit"
                            className="md:text-base text-sm px-4 py-1 my-2 rounded-2xl font-semibold bg-blue-400 text-white hover:bg-blue-500 hover:text-white hover:shadow-md flex"
                        >
                            <span>
                                <SendRoundedIcon className="pr-1 icon" />
                            </span>
                            Send
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Contact;
