import React, { useState, useEffect } from 'react';

import KeyboardArrowLeftRoundedIcon from '@mui/icons-material/KeyboardArrowLeftRounded';
import KeyboardArrowRightRoundedIcon from '@mui/icons-material/KeyboardArrowRightRounded';
import KeyboardDoubleArrowRightRoundedIcon from '@mui/icons-material/KeyboardDoubleArrowRightRounded';
import InstagramIcon from '@mui/icons-material/Instagram';
import EmailRoundedIcon from '@mui/icons-material/EmailRounded';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';

const name = "IANNE FABIANO";

const Home = () => {
    const [activeSection, setActiveSection] = useState('0');

    const [dipslayText, setDisplayText] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (currentIndex <= name.length) {
                setDisplayText(name.slice(0, currentIndex));
                setCurrentIndex((prevIndex) => prevIndex + 1);
            } else {
                clearInterval(typingInterval);
            }
        }, 300)
        return () => clearInterval(typingInterval);
    }, [currentIndex]);

    const scrollToSection = (id) => {
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        const handleScroll = () => {
            const sections = ['0', '1', '2', '3'];
            const scrollPosition = window.scrollY + 200;
            for (const section of sections) {
                const currentSection = document.getElementById(section);
                if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
                    setActiveSection(section);
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className='h-full flex flex-col justify-center gap-2 md:px-20'>
            <p className='font-bold md:text-7xl text-2xl hover:animate-bounce'>D{dipslayText}
                <span className='animate-pulse'>_</span>
            </p>

            <p className='md:text-lg text-sm'>Hey there! I'm a passionate 
                <span className='animate-pulse text-accent'>
                    <KeyboardArrowLeftRoundedIcon />
                    Web Developer
                    <KeyboardArrowRightRoundedIcon />
                </span>
                dedicated to creating elegant and user-friendly web experiences. 
            </p>

            <button onClick={() => scrollToSection('3')} className='flex font-medium py-6 text-primary hover:text-secondary'>
                <KeyboardDoubleArrowRightRoundedIcon />
                <p>Let's collaborate!</p>
            </button>

            <div className='flex gap-3'>
                <a href='https://www.instagram.com/yhianiee' target='_blank' className='hover:text-primary'>
                    <InstagramIcon />
                </a>
                <a href='mailto:fabianodianne@gmail.com' target='_blank' className='hover:text-primary'>
                    <EmailRoundedIcon />
                </a>
                <a href='https://www.linkedin.com/in/dianne-fabiano/' target='_blank' className='hover:text-primary'>
                    <LinkedInIcon />
                </a>
                <a href='https://github.com/fabianodianne' target='_blank' className='hover:text-primary'>
                    <GitHubIcon />
                </a>

            </div>
        </div>
    );
}

export default Home;
