import React, { useState, useEffect } from 'react';

import avatar from '../img/avatar.png';

import KeyboardDoubleArrowRightRoundedIcon from '@mui/icons-material/KeyboardDoubleArrowRightRounded';
import MailOutlineRoundedIcon from '@mui/icons-material/MailOutlineRounded';

const Home = () => {
    const [activeSection, setActiveSection] = useState('0');

    const scrollToSection = (id) => {
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        const handleScroll = () => {
            const sections = ['0', '1', '2'];
            const scrollPosition = window.scrollY + 200;
            for (const section of sections) {
                const currentSection = document.getElementById(section);
                if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
                    setActiveSection(section);
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className=" h-fit grid md:grid-cols-2 gap-4 p-10 pt-20">
            <div className='flex flex-col gap-2'>
                <p className='text-lg'>Hey there! I'm <span className='text-blue-400 '>
                    <span className='hover:text-blue-500 hover:font'>D</span>
                    </span>.</p>
                <p>I love creating elegant web experiences and captivating interfaces. I'm also passionate about developing beautiful mobile apps with innovative functionality.</p>
                <button onClick={() => scrollToSection('2')} className='flex text-sky-800 hover:pl-2'>
                    <KeyboardDoubleArrowRightRoundedIcon />
                    <p>Let's collaborate!</p>
                </button>
            </div>

            <div>
                <img src={avatar} className='animate-bounce px-14' />
            </div>

        </div>
    );
}

export default Home;
