import React, { useState, useEffect, useRef } from 'react';

import backgroundImage from './img/bg.png';
import Header from './components/header';
// import Nav from './components/nav';
import About from './components/about';

function App() {
  const [ activeSection, setActiveSection ] = useState('0');

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if(element) {
      element.scrollIntoView({behavior:'smooth'});
    }
  };

  useEffect(() => {

    const handleScroll = () => {
      const sections = ['0', '1', '2'];
      const scrollPosition = window.scrollY + 200;

      for (const section of sections) {
        const currentSection = document.getElementById(section);
        if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
          setActiveSection(section);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="bg-blue-200 bg-cover bg-center h-screen w-screen px-60 py-4" 
      style={{
        backgroundImage: "url(${backgroundImage})",
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <div className='bg-purple-50 h-full rounded-lg text-dark font-mono drop-shadow-md border'>
        <div className='w-full bg-white backdrop-blur-sm rounded-t-lg text-center py-1 sticky'>
          <p className='font-semibold text-sm'>BRING YOUR VISIONS TO LIFE</p>
        </div>
        <Header />

        <nav className='bg-blue-400 flex justify-center'>
          <ul className='flex justify-between'>
            <li>
              <a>About</a>
            </li>
            <li>
              <a>Works</a>
            </li>
            <li>
              <a>Background</a>
            </li>
            <li>
              <a>Education</a>
            </li>
            <li>
              <a>Contact</a>
            </li>
          </ul>
        </nav>


        {/* <Nav /> */}
        <About />
      </div>
    </div>
  );
}

export default App;
