import React, { useState, useEffect } from 'react';

import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import SourceRoundedIcon from '@mui/icons-material/SourceRounded';
import WorkRoundedIcon from '@mui/icons-material/WorkRounded';
import SchoolRoundedIcon from '@mui/icons-material/SchoolRounded';
import RingVolumeRoundedIcon from '@mui/icons-material/RingVolumeRounded';

function Navbar() {
    const [activeSection, setActiveSection] = useState(0);

    const scrollToSection = (id) => {
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        const handleScroll = () => {
            const sections = ['0', '1', '2', '3', '4'];
            const scrollPosition = window.scrollY + 200;
            for (const section of sections) {
                const currentSection = document.getElementById(section);
                if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
                    setActiveSection(section);
                }
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <nav className='bg-white/50 backdrop-blur-sm flex justify-center p-1 sticky top-0 md:text-sm text-xs'>
          <ul className='flex justify-between gap-10'>
            <li  className={`{activeSection === '0' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
                <div className='w-full flex justify-center md:hidden'>
                  <HomeRoundedIcon />
                </div>
                <button
                    className={`${activeSection === '0' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                    onClick={() => scrollToSection('0')}  
                >
                    Home
                </button>
            </li>

            <li className={`{activeSection === '1' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
                    <div className='w-full flex justify-center md:hidden'>
                        <SourceRoundedIcon />
                </div>
              <button
                className={`${activeSection === '1' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('1')}
              >Works</button>
            </li>

            <li  className={`{activeSection === '2' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
                    <div className='w-full flex justify-center md:hidden'>
                        <WorkRoundedIcon />
                </div>
              <button
                className={`${activeSection === '2' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('2')}
              >Background</button>
            </li>

            <li  className={`{activeSection === '3' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
                    <div className='w-full flex justify-center md:hidden'>
                    <SchoolRoundedIcon />
                </div>
              <button
                className={`${activeSection === '3' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('3')}
              >Education</button>
            </li>

            <li className={`{activeSection === '4' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
                <div className='w-full flex justify-center md:hidden'>
                        <RingVolumeRoundedIcon />
                </div>
              <button
                className={`${activeSection === '4' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('4')}
              >Contact</button>
            </li>
          </ul>
        </nav>
    );
}

export default Navbar;
