import React, { useState } from 'react';

import ArrowCircleUpIcon from '@mui/icons-material/ArrowCircleUp';
import ArrowCircleUpTwoToneIcon from '@mui/icons-material/ArrowCircleUpTwoTone';

const Footer = () => {
    const [ showIcon, setShowIcon ] = useState(false);

    const scrollToTop = () => {
        const c = document.documentElement.scrollTop || document.body.scrollTop;
        if (c > 0) {
            window.requestAnimationFrame(scrollToTop);
            window.scrollTo(0, c - c / 8);
        }
    };

    const handleScrollToTop = () => {
        scrollToTop();
    };

    return (
        <div>

            <div className="flex md:flex-row flex-col justify-evenly gap-3">

                <div className="md:w-1/2">
                    <p className="md:text-xl text-lg font-bold">DIANNE MAE P FABIANO</p>
                    <p className="md:text-lg text-base font-medium">Web Developer</p>
                </div>

                <div className="md:w-1/2">
                    socials 
                </div>

            </div>

            <button
                onClick={handleScrollToTop}
                className="mt-10 flex justify-center scroll-up"
                onMouseEnter={() => setShowIcon(true)}
                onMouseLeave={() => setShowIcon(false)}
            >
                {!showIcon && <ArrowCircleUpIcon className='text-primary animate-bounce' />}
                {showIcon && <ArrowCircleUpTwoToneIcon className='text-secondary animate-bounce' />}
            </button>

        </div>
    );
};

export default Footer;
