import './App.css';

import React, { useState, useEffect, useRef } from 'react';

import backgroundImage from './img/bg.png';
import Header from './components/header';
import Nav from './components/nav';
import About from './components/about';

function App() {
  const [ activeSection, setActiveSection ] = useState('0');

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if(element) {
      element.scrollIntoView({behavior:''})
    }
  };

  return (
    <div className="bg-blue-200 bg-cover bg-center h-screen w-screen px-60 py-4" 
      style={{
        backgroundImage: "url(${backgroundImage})",
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <div className='bg-purple-50 h-full rounded-lg text-dark font-mono drop-shadow-md border'>
        <div className='w-full bg-white backdrop-blur-sm rounded-t-lg text-center py-1 sticky'>
          <p className='font-semibold text-sm'>BRING YOUR VISIONS TO LIFE</p>
        </div>
        <Header />
        <Nav />
        <About />
      </div>
    </div>
  );
}

export default App;
