import React, { useState } from "react";

const Contact = () => {
    const [ formData, setFormData ] = useState({
        name: '',
        email: '',
        subject: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormData({ name: '', email: '', subject: '', message: ''})
    }

    return (
        <div className="grid md:grid-cols-3 p-10 py-20 gap-4"> 
            <div>
                <p className="text-md font-semibold">Interested in collaborating with me?</p>
                <p className="text-sm">Let's turn your ideas into reality!</p>

            </div>

            <div className="md:col-span-2 shadow hover:shadow-md rounded-md">
                <form onSubmit={handleSubmit}>
                    <div className="p-4">
                        <input
                            className="border border-slate-300 rounded-xl w-full p-2 shadow focus:border"
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            placeholder="Name"
                            required
                        />
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Contact;
