import React, { useState } from "react";

const Contact = () => {
    const [ formData, setFormData ] = useState({
        name: '',
        email: '',
        subject: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData
    };

    return (
        <div className="grid md:grid-cols-3 p-10 py-20 gap-4"> 
            <div>
                <p className="text-md font-semibold">Interested in collaborating with me?</p>
                <p className="text-sm">Let's turn your ideas into reality!</p>

            </div>

            <div className="md:col-span-2 shadow hover:shadow-md rounded-md">
                <form>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={FormData.name}
                        onChange={handleChange}
                        required
                    />
                </form>
            </div>
        </div>
    );
};

export default Contact;
