import React, { useState, useEffect } from 'react';
import avatar from '../img/avatar.png';


import KeyboardArrowLeftRoundedIcon from '@mui/icons-material/KeyboardArrowLeftRounded';
import KeyboardArrowRightRoundedIcon from '@mui/icons-material/KeyboardArrowRightRounded';
import KeyboardDoubleArrowRightRoundedIcon from '@mui/icons-material/KeyboardDoubleArrowRightRounded';
import ArrowDownwardRoundedIcon from '@mui/icons-material/ArrowDownwardRounded';

const name = "IANNE FABIANO";

const Home = () => {
    const [activeSection, setActiveSection] = useState('0');

    const [dipslayText, setDisplayText] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (currentIndex <= name.length) {
                setDisplayText(name.slice(0, currentIndex));
                setCurrentIndex((prevIndex) => prevIndex + 1);
            } else {
                clearInterval(typingInterval);
            }
        }, 300)
        return () => clearInterval(typingInterval);
    }, [currentIndex]);

    const scrollToSection = (id) => {
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        const handleScroll = () => {
            const sections = ['0', '1', '2', '3'];
            const scrollPosition = window.scrollY + 200;
            for (const section of sections) {
                const currentSection = document.getElementById(section);
                if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
                    setActiveSection(section);
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className='h-full flex flex-col justify-center text-dark'>
            <div className='hflex flex-col bg-accent items-center'>
                
                <div className='flex flex-col gap-3 items-center'>
                    <p className='title font-bold md:text-6xl text-3xl text-center'>D{dipslayText}</p>

                    <p className='title font-medium animate-pulse md:text-2xl text-xl'>
                        <span>
                            <KeyboardArrowLeftRoundedIcon />
                        </span>
                        Web Developer
                        <span>
                            <KeyboardArrowRightRoundedIcon />
                        </span>
                    </p>
                </div>

                <button onClick={() => scrollToSection('1')} 
                    className='bg-accent shadow hover:shadow-md hover:border-2 hover:border-bg1 rounded-full p-2 animate-bounce'
                >
                    <span>
                        <ArrowDownwardRoundedIcon />
                    </span>
                </button>
            </div>


        </div>
    );
}

export default Home;
