import './App.css';
import Home from './components/Home';
import Navbar from './components/Nav';
import About from './components/About';
import Works from './components/Works';
import Expertise from './components/Expertise';
import Contact from './components/Contact';
import Footer from './components/Footer';

import BoltIcon from '@mui/icons-material/Bolt';

function App() {

  const handleReload = () => {
    window.location.reload();
    window.scrollTo(0, 0);
  };

  return (
    <body className="scrollbar bg-accent h-screen w-screen lg:px-40 px-8 py-2 overflow-hidden bg-center">
      <div className='w-full h-fit bg-bg2 rounded-t-lg text-center flex justify-center pt-1 sticky top-0 z-50 text-sm'>
        <span className='md:hidden block'>
          <p className="absolute left-2 top-0 text-base font-serif">D.</p>
        </span>

        <button onClick={handleReload} className='font-semibold'>BRING YOUR VISIONS TO LIFE</button>

        <span className='absolute right-1 top-0.5 flex items-center animate-pulse'>
          <BoltIcon className='pl-1 pb-1 icon text-green-500' />
          <p className='pr-1 hidden md:block'>Available for Hire</p>
        </span>
      </div>

      <div className='scrollbar bg-white h-full overflow-y-scroll overflow-x-hidden rounded-lg rounded-t-none text-dark shadow'>

        <div>

          <Navbar />
          <div className=' scroll-pt-2.5 snap-y snap-start'>
            <section className='px-10 h-screen bg-bg2'>
              <Home />
            </section>

            <section id='0' className='px-10 bg-bg1'>
              <About />
            </section>

            <section id='1' className='px-10 pt-16'>
              <Expertise />
            </section>

            <section id='2' className='px-10 pt-16'>
              <Works />
            </section>

            <section id='3' className='px-10 pt-16 pb-10'>
              <Contact />
            </section>

            <section className='px-10 py-5 bg-white/50'>
              <Footer />
            </section>

          </div>
        </div>
      </div>

    </body>
  );
}

export default App;
