import React, { useState, useEffect } from 'react';

import HomeRoundedIcon from '@mui/icons-material/HomeRounded';

function Navbar() {
    const [activeSection, setActiveSection] = useState(0);

    const scrollToSection = (id) => {
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    useEffect(() => {
        const handleScroll = () => {
            const sections = ['0', '1', '2', '3', '4'];
            const scrollPosition = window.scrollY + 200;
            for (const section of sections) {
                const currentSection = document.getElementById(section);
                if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
                    setActiveSection(section);
                }
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <nav className='bg-white/50 backdrop-blur-sm flex justify-center p-1 sticky top-0'>
          <ul className='flex justify-between gap-8'>
            <li 
                className={`{activeSection === '0' ? 'active' : ''} hover:text-purple-500 cursor-pointer flex flex-col justify-center justify-item-center bg-blue-200`}>
                <HomeRoundedIcon />
                <button
                    className={`${activeSection === '0' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                    onClick={() => scrollToSection('0')}  
                >
                    Home
                </button>
            </li>
            <li
              className={`{activeSection === '1' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '1' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('1')}
              >Works</button>
            </li>
            <li
              className={`{activeSection === '2' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '2' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('2')}
              >Background</button>
            </li>
            <li
              className={`{activeSection === '3' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '3' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('3')}
              >Education</button>
            </li>
            <li
              className={`{activeSection === '4' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '4' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('4')}
              >Contact</button>
            </li>
          </ul>
        </nav>
    );
}

export default Navbar;
