import React, { useState, useEffect, useRef } from 'react';

import './App.css';
import backgroundImage from './img/bg.png';
import Header from './components/header';
// import Nav from './components/nav';
import About from './components/about';

function App() {
  const [ activeSection, setActiveSection ] = useState('0');

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if(element) {
      element.scrollIntoView({behavior:'smooth'});
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['0', '1', '2', '3', '4'];
      const scrollPosition = window.scrollY + 200;
      for (const section of sections) {
        const currentSection = document.getElementById(section);
        if (currentSection && scrollPosition >= currentSection.offsetTop && scrollPosition < currentSection.offsetTop + currentSection.offsetHeight) {
          setActiveSection(section);
        }
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleReload = () => {
    window.location.reload();
    window.scrollTo(0, 0);
  };

  return (
    <div className="scrollbar bg-blue-200 h-f w-screen px-60 py-4 overflow-hidden">

      <div className='w-full bg-white backdrop-blur-sm rounded-t-lg text-center py-1'>
        <p className='font-semibold text-sm'>BRING YOUR VISIONS TO LIFE</p>
      </div>

      <div className='scrollbar bg-purple-50 h-screen overflow-y-scroll rounded-b-lg text-dark font-mono drop-shadow-md border'>
        <Header />

        <nav className='bg-white/50 backdrop-blur-sm flex justify-center p-2 sticky top-0'>
          <ul className='flex justify-between gap-8'>
            <li 
              className={`{activeSection === '0' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '0' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('0')}  
              >About</button>
            </li>
            <li
              className={`{activeSection === '1' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '1' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('1')}
              >Works</button>
            </li>
            <li
              className={`{activeSection === '2' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '2' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('2')}
              >Background</button>
            </li>
            <li
              className={`{activeSection === '3' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '3' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('3')}
              >Education</button>
            </li>
            <li
              className={`{activeSection === '4' ? 'active' : ''} hover:text-purple-500 cursor-pointer`}>
              <button
                className={`${activeSection === '4' ? 'text-purple-400 font-semibold hover:text-purple-500' : ''}`}
                onClick={() => scrollToSection('4')}
              >Contact</button>
            </li>
          </ul>
        </nav>

        <div className='scroll-container'>
          <section id='0' className='bg-blue-200'>
            <About />
          </section>

          <section id='1' className='bg-blue-300 h-screen'>
            works
          </section>

          <section id='2' className='bg-blue-400 h-screen'>
            background
          </section>

          <section id='3' className='bg-blue-500 h-screen'>
            education
          </section>

          <section id='4' className='bg-blue-600 h-screen'>
            contact
          </section>

        </div>
      </div>
    </div>
  );
}

export default App;
