import React, { useState, useEffect } from 'react';
import './App.css';
import Loading from './components/LoadingPage';
import Home from './components/Home';
import Navbar from './components/Nav';
import Works from './components/Works';
import Projects from './components/Projects';
import Expertise from './components/Expertise';
import Contact from './components/Contact';
import Footer from './components/Footer';

import BoltIcon from '@mui/icons-material/Bolt';

function App() {
  const [ isLoading, setIsLoading ] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2800);
    return () => clearTimeout(timer);
  }, []); 

  return (
    <body className="scrollbar bg-primary/20 dark:bg-gray-950  dark:text-light text-text h-screen w-screen xl:px-40 lg:px-20 px-8 py-3 overflow-hidden">
      {isLoading && <Loading />}

      {!isLoading && 
        <div className='bg-white dark:bg-dark h-full scrollbar overflow-y-scroll overflow-x-hidden rounded-lg shadow'>

          <div className='w-full h-fit bg-primary text-text rounded-t-lg text-center flex justify-center p-1 sticky top-0 z-50 text-xs'>
            <button className='font-semibold mono'>BRING YOUR VISIONS TO LIFE</button>

            <span className='absolute right-1 top-0 flex items-center animate-pulse'>
              <BoltIcon className='pl-1 pb-1 icon text-green-500' />
              <p className='pr-1 hidden md:block text-xs'>Available for Hire</p>
            </span>
          </div>

          <Navbar />

          <section id='0' className='home px-10 flex flex-col justify-center'>
            <Home />
          </section>

          <section id='1' className='view px-10 pt-16'>
            <Expertise />
          </section>

          <section id='2' className='view px-10 pt-16 bg-pink'>
            {/* <Works /> */}
            <Projects />
          </section>

          <section id='3' className='view px-10 pt-16 pb-10'>
              <p className='text-center font-semibold pb-4 text-xl mono'>CONTACT ME</p>
            <Contact />
          </section>

          <section className='px-10 pt-5 pb-2 bottom-0'>
            <Footer />
          </section>


        </div>
      }
    </body>
  );
}

export default App;
