import React, { useState, useEffect } from 'react';

import explorer from '../img/explorer.png';

const str = "HELLO WORLD!";

const Loading = () => {
    const [dipslayText, setDisplayText] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (currentIndex <= str.length) {
                setDisplayText(str.slice(0, currentIndex));
                setCurrentIndex((prevIndex) => prevIndex + 1);
            } else {
                clearInterval(typingInterval);
            }
        }, 300)
        return () => clearInterval(typingInterval);
    }, [currentIndex]);

    useEffect(() => {
        const img = new Image();
        imageListClasses.src = explorer;

    }, [img]);

    return (
        <div className='h-screen flex flex-col justify-center text-center mono font-bold text-4xl'>
            $ {dipslayText}
            <div>
                HELLO
            </div>
        </div>
    );
};

export default Loading;
