import React, { useEffect, useState } from 'react';

import iconImage from '../img/icon.jpg';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import KeyboardArrowLeftRoundedIcon from '@mui/icons-material/KeyboardArrowLeftRounded';
import KeyboardArrowRightRoundedIcon from '@mui/icons-material/KeyboardArrowRightRounded';
import EmailRoundedIcon from '@mui/icons-material/EmailRounded';

const name = "IANNE FABIANO";

const Header = () => {
    const [ dipslayText, setDisplayText ] = useState('');
    const [ currentIndex, setCurrentIndex ] = useState(0);

    useEffect(() => {
        const typingInterval = setInterval(() => {
            if (currentIndex <= name.length) {
                setDisplayText(name.slice(0, currentIndex));
                setCurrentIndex((prevIndex) => prevIndex + 1);
            } else {
                clearInterval(typingInterval);
            }
        }, 300)
        return () => clearInterval(typingInterval);
    }, [currentIndex]);

    return (
        <div className="h-fit py-6 md:px-24 px-8 flex flex-col sm:flex-row">
            <div className='aspect-square sm:h-20 md:h-32 h-16 mr-8'>
                <img src={iconImage} className="rounded-full h-full hover:shadow-md" />
            </div>



            <div cl>
                <p className='font-black text-dark lg:text-xl text-lg'>D{dipslayText}</p>

                <p className='font-semibold animate-pulse'>
                    <span>
                        <KeyboardArrowLeftRoundedIcon />
                    </span>
                    Web Developer
                    <span>
                        <KeyboardArrowRightRoundedIcon />
                    </span>
                </p>
            </div>


            <div className='flex gap-2'>
                <a
                    href="mailto:fabianodianne@gmail.com"
                    className='text-primary hover:text-secondary'>
                    <EmailRoundedIcon />
                </a>

                <a
                    href='https://www.linkedin.com/in/dianne-fabiano/'
                    className='text-primary hover:text-secondary'
                >
                    <LinkedInIcon />
                </a>

                <a
                    href='https://github.com/fabianodianne'
                    className='text-primary hover:text-secondary'
                >
                    <GitHubIcon />
                </a>

            </div>
        </div>
    );
};

export default Header;
